    public static void main(String[] args) {
        System.out.println("Usage: OfflineVerify <signed attestation statement>");
//        Json j = new Json();
        System.out.println("JWS : " + jwsString);

        JsonWebSignature jws = null;
        try {
            jws = JsonWebSignature.parser(JacksonFactory.getDefaultInstance())
                    .setPayloadClass(AttestationStatement.class).parse(jwsString);
            System.out.println("JWS Header: " + jws.getHeader());

            System.out.println("JWS Payload: " + jws.getPayload());

            X509Certificate cert;
            try {
                cert = jws.verifySignature();
                if (cert == null) {
                    System.err.println("Failure: Signature verification failed.");
                }
            } catch (GeneralSecurityException e) {
                System.err.println(
                        "Failure: Error during cryptographic verification of the JWS signature.");
            }

            new DefaultHostnameVerifier().verify("", null);
        } catch (IOException e) {
            System.err.println("Failure: " + jwsString + " is not valid JWS " +
                    "format.");
            e.printStackTrace();
        }

    }

